def scale_images(data):
  image = data['image']
  return image / 255

ds = ds.map(scale_images)
ds = ds.map(lambda image: (tf.image.resize(image, (res_pix, res_pix))))
ds = ds.cache()
ds = ds.shuffle(1000)
ds = ds.batch(128)
ds = ds.prefetch(64)

ds.as_numpy_iterator().next().shape

def build_generator():
  model = Sequential()
  conv2d_dim = 128

  model.add(Dense(7*7*128, input_dim=128))
  model.add(LeakyReLU(0.2))
  model.add(Reshape((7,7,128)))

  if res_pix >= 14:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 5, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 28:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 5, padding='same'))
    model.add(LeakyReLU(0.2))
    model.add(Conv2D(conv2d_dim, 4, padding='same'))
    model.add(LeakyReLU(0.2))
    model.add(Conv2D(conv2d_dim, 3, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 56:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 5, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 112:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 5, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 224:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 4, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 448:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 4, padding='same'))
    model.add(LeakyReLU(0.2))

  if res_pix >= 896:
    model.add(UpSampling2D())
    model.add(Conv2D(conv2d_dim, 4, padding='same'))
    model.add(LeakyReLU(0.2))


  model.add(Conv2D(1, 4, padding='same', activation='sigmoid'))



  return model

generator = build_generator()
generator.summary()

img = generator.predict(np.random.randn(num_images, 128, 1))

fig, ax = plt.subplots(ncols=num_images, figsize=(res_post, res_post))
for idx, img in enumerate(img):
  ax[idx].imshow(np.squeeze(img))
  ax[idx].title.set_text(idx)

def build_discriminator():
  model = Sequential()

  model.add(Conv2D(32, 5, input_shape = (res_pix, res_pix, 1)))
  model.add(LeakyReLU(0.2))
  model.add(Dropout(0.4))

  model.add(Conv2D(64, 5))
  model.add(LeakyReLU(0.2))
  model.add(Dropout(0.4))

  model.add(Conv2D(128, 5))
  model.add(LeakyReLU(0.2))
  model.add(Dropout(0.4))

  model.add(Conv2D(256, 5))
  model.add(LeakyReLU(0.2))
  model.add(Dropout(0.4))

  model.add(Flatten())
  model.add(Dropout(0.4))
  model.add(Dense(1, activation='sigmoid'))

  return model

discriminator = build_discriminator()

discriminator.summary()

discriminator.predict(np.expand_dims(img,0))

g_opt = Adam(learning_rate=0.0001)
d_opt = Adam(learning_rate=0.00001)

g_loss = BinaryCrossentropy()
d_loss = BinaryCrossentropy()

class FashionGAN(Model):
  def __init__(self, generator, discriminator, *args, **kwargs):
    super().__init__(*args, **kwargs)
    self.generator = generator
    self.discriminator = discriminator

  def compile(self, g_opt, d_opt, g_loss, dloss, *args, **kwargs):
    super().compile(*args, **kwargs)

    self.g_opt = g_opt
    self.d_opt = d_opt
    self.g_loss = g_loss
    self.d_loss = d_loss

  def train_step(self, batch):
    real_images = batch
    fake_images = self.generator(tf.random.normal((128, 128, 1)))

    with tf.GradientTape() as d_tape:
      yhat_real = self.discriminator(real_images, training=True)
      yhat_fake = self.discriminator(fake_images, training=True)
      yhat_realfake = tf.concat([yhat_real, yhat_fake], axis=0)

      print('labels for real and fake images')
      y_realfake = tf.concat([tf.zeros_like(yhat_real), tf.ones_like(yhat_fake)], axis=0)

      print('add noise to the true outputs. fuzz the discriminator')
      noise_real = 0.15*tf.random.uniform(tf.shape(yhat_real))
      noise_fake = -0.15*tf.random.uniform(tf.shape(yhat_fake))
      y_realfake += tf.concat([noise_real, noise_fake], axis=0)

      print('calculate loss')
      total_d_loss = self.d_loss(y_realfake, yhat_realfake)

    print('backpropagation')
    dgrad = d_tape.gradient(total_d_loss, self.discriminator.trainable_variables)
    self.d_opt.apply_gradients(zip(dgrad, self.discriminator.trainable_variables))

    with tf.GradientTape() as g_tape:
      print('generate new images')
      gen_images = self.generator(tf.random.normal((128, 128, 1)), training=True)

      print('create the predicted labels')
      predicted_labels = self.discriminator(gen_images, training=False)

      print('calculate loss. fuzz the discriminator.')
      total_g_loss = self.g_loss(tf.zeros_like(predicted_labels), predicted_labels)

      ggrad = g_tape.gradient(total_g_loss, self.generator.trainable_variables)
      self.g_opt.apply_gradients(zip(ggrad, self.generator.trainable_variables))

      return {"d_loss":total_d_loss, "g_loss":total_g_loss}


  def test_step(self):
    pass

fashgan = FashionGAN(generator, discriminator)

fashgan.compile(g_opt, d_opt, g_loss, d_loss)

class ModelMonitor(Callback):
  def __init__(self, num_img=num_images, latent_dim=128):
    self.num_img = num_img
    self.latent_dim = latent_dim

  def on_epoch_end(self, epoch, logs=None):
    print('-- epoch end --')
    random_latent_vectors = tf.random.uniform((self.num_img, self.latent_dim, 1))
    generated_images = self.model.generator(random_latent_vectors)
    generated_images *= 255
    generated_images.numpy()
    for i in range(self.num_img):
      img = array_to_img(generated_images[i])
      if not os.path.exists('images'):
        os.makedirs('images')
      img.save(os.path.join('images', f'generated_img_{epoch}_{i}.png'))

      Image(f'images/generated_img_{epoch}_{i}.png')
      file_img = plt.imread(f'images/generated_img_{epoch}_{i}.png')
      plt.figure(figsize=(res_pix, res_pix))
      plt.imshow(file_img)
      plt.axis('off')
      plt.show()

  def on_batch_end(self, batch, logs=None):
    pass
