def generate_images(num_x, num_y, size_x, size_y):
  generated_images = generator.predict(tf.random.normal((16, 128, 1)))
  fig, ax = plt.subplots(nrows=num_x, ncols=num_y, figsize=(size_x, size_y))
  for row in range(num_x):
    for col in range(num_y):
      ax[row][col].imshow(generated_images[(row+1)*(col+1)-1])
      ax[row][col].title.set_text(fig)

  return generated_images

